INSERT INTO `board` VALUES (170,47,'A. 동기란 무엇인가','2018-03-02 13:00:38','<p class=\"0\"><span lang=\"EN-US\" data-mce-style=\"mso-fareast-font-family: 함초롬바탕; mso-font-width: 100%; letter-spacing: 0pt; mso-text-raise: 0pt; font-weight: bold; font-size: 11.0pt;\" style=\"letter-spacing: 0pt; font-weight: bold; font-size: 11pt;\">1) </span><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕; font-weight: bold; font-size: 11.0pt;\" style=\"font-family: 함초롬바탕; font-weight: bold; font-size: 11pt;\">동기란 무엇인가</span></p><p class=\"0\"><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕;\" style=\"font-family: 함초롬바탕;\">동기</span><span lang=\"EN-US\" data-mce-style=\"mso-fareast-font-family: 함초롬바탕; mso-font-width: 100%; letter-spacing: 0pt; mso-text-raise: 0pt;\" style=\"letter-spacing: 0pt;\">(motivation)</span><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕;\" style=\"font-family: 함초롬바탕;\">란 목표지향적인 활동이 유발되고 지속되는 과정</span></p><p class=\"0\"><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕;\" style=\"font-family: 함초롬바탕;\">학습자 동기는 학교에서 시험을 치르는 행동과 시험에서의 성공 모두에 영향을 주는 중요한 요인</span></p><p class=\"0\"><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕;\" style=\"font-family: 함초롬바탕;\">일반적으로 동기화된 학생들은</span></p><p class=\"0\"><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕; mso-font-width: 100%; letter-spacing: 0pt; mso-text-raise: 0pt;\" style=\"font-family: 함초롬바탕; letter-spacing: 0pt;\">⓵ </span><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕;\" style=\"font-family: 함초롬바탕;\">학교에 대해 긍정적인 태도를 가지고 있고 학교가 만족스럽다고 말한다</span><span lang=\"EN-US\" data-mce-style=\"mso-fareast-font-family: 함초롬바탕; mso-font-width: 100%; letter-spacing: 0pt; mso-text-raise: 0pt;\" style=\"letter-spacing: 0pt;\">.</span></p><p class=\"0\"><span data-mce-style=\"font-family: 함초롬바탕; mso-fareast-font-family: 함초롬바탕; mso-font-width: 100%; letter-spaci